import React, { useEffect, useState } from "react";
import axios from "axios";

export const Home = () => {
    const [posts, setPosts] = useState([]);
    useEffect(() => { // to get all posts everytime the page is loaded
        const fetchPosts = async () => {
            try {
                const response = await axios.get("http://localhost:3001/posts");
                setPosts(response.data); // return the posts from the database
                console.log(response.data);
            } catch (error) {
                console.log(error);
            }
        };
        fetchPosts();
    }, []);
    return (
        <div>
            <h1>Listings</h1>
            <ul>
                {posts.map((post) => (
                    <li key={post._id}>
                        <h2>{post.name}</h2>
                        <button onC>Bookmark</button>
                        <p>{post.address}</p>
                        <p>{post.rent}</p>
                        <p>{post.description}</p>
                        <img src={post.image} alt="apartment image" />
                        <p>Phone Number: {post.contact}</p>
                    </li>
                ))}
            </ul>

        </div>
    );
    }
